import Layout from '../components/Layout';
import NewsItem from '../components/NewsItem';
import ApiPages from '../components/ApiPages';
import React,{ useState , useEffect } from 'react';
import getData from '../components/GetData';

const Api = (props,page) => {
    var data
    let arrv = [];
    console.log(data)

    for(let i of data.articles)
    {
        arrv.push(
         <NewsItem key={i.url} props = {i}/>
        )
    }
    
    return (
        <Layout>
            <h2>NEWS</h2>
            {arrv}
            <ApiPages callbackfn = {currPage}/>
            </Layout>
    )
}
Api.getInititalProps = async function(){
    {
        var url = 'https://newsapi.org/v2/everything?' +
        'q=tech&' +
        'sortBy=popularity&'+
        'page='+page+'&'+
        'pageSize=1&'+
        'apiKey=3b214239993247f18926b8fab6ee014f';
        const res = await fetch(url);
        const data = await res.json();
        return data
    }
}
export default Api;
