import Layout from '../components/Layout';
import NewsItem from '../components/NewsItem';
import ApiPages from '../components/ApiPages';
import { useState, useEffect } from 'react';
import getData from '../components/GetData';

const Api = () => {
    const [page, setPage] = useState(1)
    var data
    useEffect(() => {
        return () => {
            var url = 'https://newsapi.org/v2/everything?' +
                'q=tech&' +
                'sortBy=popularity&' +
                'page=' + page + '&' +
                'pageSize=1&' +
                'apiKey=3b214239993247f18926b8fab6ee014f';
            const res = fetch(url);
            data = res.json();
        };
    })
    let arrv = [];
    console.log(data)
    const currPage = (p) => {
        setPage(p)
        data = getData(page)
    }

    /*for(let i of data.articles)
    {
        arrv.push(
         <NewsItem key={i.url} props = {i}/>
        )
    }*/



    return (
        <Layout>
            <h2>NEWS</h2>
            {arrv}
            <ApiPages callbackfn={currPage} />
        </Layout>
    )
}

export default Api;
