import Head from "next/head";
import fetch from "isomorphic-unfetch";
import { useEffect, useState, useRef } from "react";
import { connect } from "react-redux";
import InfiniteScroll from "react-infinite-scroller";
import Api from "../components/Api";
import Layout from "../components/Layout";
import Search from "../components/Search";
import Pagination from "../components/Pagination";
import { changePage } from "../components/actions/pageAction";

const ApiPages = props => {
  console.log(props.page);
  const [data, setData] = useState(props.data);
  const mounted = useRef();
  useEffect(() => {
    if (!props.isFetched || mounted.current)
      ApiPages.getInitialProps({
        page: props.page,
        search: props.search,
        isUser: 1
      }).then(json => {
        var temp = Object.assign([], data);
        for (var i = data.length; i < data.length + json.data.length; i++) {
          console.log("hmm");
          temp[i] = json.data[i - data.length];
        }
        setData(temp);
      });
    else mounted.current = true;
  }, [props.search]);

  const loadFunc = page => {
    console.log('called')
    props.changePage(page);
  };

  const st = {
    height: "1000px"
  };
  return (
    <>
      <Head>
        <meta keywords="API" />
      </Head>
      <Layout>
        <h2>NEWS</h2>
        <br />
        <Search />
        <br />
        <Pagination />
        <InfiniteScroll
          pageStart={1}
          loadMore={loadFunc}
          hasMore={true}
          threshold={0}
          loader={
            <div className="loader" key={0}>
              Loading ...
            </div>
          }
        >
          <div style={st}>></div>
          {/* <Api data={data} /> */}
        </InfiniteScroll>
        <Pagination />
      </Layout>
    </>
  );
};

ApiPages.getInitialProps = async function({
  search = "tech",
  page = 1,
  isUser = 0
}) {
  if (isUser == 0 && !(typeof window === "undefined"))
    return { data: {}, isFetched: false };

  var url =
    "https://newsapi.org/v2/everything?" +
    "q=" +
    search +
    "&" +
    "sortBy=publishedAt&" +
    "page=" +
    page +
    "&" +
    "pageSize=10&language=en&" +
    "apiKey=3b214239993247f18926b8fab6ee014f";
  const res = await fetch(url);
  const json = await res.json();
  return { data: json.articles, isFetched: true };
};
const mapStateToProps = state => ({
  page: state.page.page,
  search: state.page.search
});

export default connect(
  mapStateToProps,
  { changePage }
)(ApiPages);
