import React, { Component } from 'react'

export class ApiPages extends Component {

    static getInitialProps = async function () {
        console.log(this.props.page)
        var url = 'https://newsapi.org/v2/everything?' +
            'q=' + 'tech' + '&' +
            'sortBy=publishedAt&' +
            'page=' + '1' + '&' +
            'pageSize=5&language=en&' +
            'apiKey=3b214239993247f18926b8fab6ee014f';
        const res = await fetch(url);
        const json = await res.json()
        return { data: json }

    }
    render() {
        return (
            <>
                <Head>
                    <meta keywords="API" />
                </Head>
                <Layout>
                    <h2>NEWS</h2>
                    <br />
                    <Search />
                    <br />
                    <Pagination />
                    <Api data={this.props.data} />
                    <Pagination />
                </Layout>
            </>
        )
    }
}

const mapStateToProps = state => ({
    page: state.page.page,
    search: state.page.search
})

export default connect(mapStateToProps, null)(ApiPages)
