import LazyLoad from 'react-lazy-load';

const imgStyle = {
    // fontSize: '15px',
    // textAlign: 'center'
    height:'200px',
    width:'100%',
    display:'block'
};

const NewsItem = ({ article }) => {
    return (
        <>
            <div className="card mb-3">
                <div className="card-body">
                    <h5 className="card-title"><b><u>{article.title}</u></b></h5>
                    <h6 className="card-subtitle text-muted">{article.publishedAt}</h6>
                </div>
                <LazyLoad
                    width={100}
                    height={100}
                    debounce={false}
                    offsetVertical={500}
                >
                     <img style={imgStyle} src="data:image/svg+xml;charset=UTF-8,%3Csvg%20width%3D%22318%22%20height%3D%22180%22%20xmlns%3D%22http%3A%2F%2Fwww.w3.org%2F2000%2Fsvg%22%20viewBox%3D%220%200%20318%20180%22%20preserveAspectRatio%3D%22none%22%3E%3Cdefs%3E%3Cstyle%20type%3D%22text%2Fcss%22%3E%23holder_158bd1d28ef%20text%20%7B%20fill%3Argba(255%2C255%2C255%2C.75)%3Bfont-weight%3Anormal%3Bfont-family%3AHelvetica%2C%20monospace%3Bfont-size%3A16pt%20%7D%20%3C%2Fstyle%3E%3C%2Fdefs%3E%3Cg%20id%3D%22holder_158bd1d28ef%22%3E%3Crect%20width%3D%22318%22%20height%3D%22180%22%20fill%3D%22%23777%22%3E%3C%2Frect%3E%3Cg%3E%3Ctext%20x%3D%22129.359375%22%20y%3D%2297.35%22%3EImage%3C%2Ftext%3E%3C%2Fg%3E%3C%2Fg%3E%3C%2Fsvg%3E" alt="Card image" />
                    {/* <img style={imgStyle} src={article.urlToImage} charset=UTF-8,%3Csvg%20width%3D%22318%22%20height%3D%22180%22%20xmlns%3D%22http%3A%2F%2Fwww.w3.org%2F2000%2Fsvg%22%20viewBox%3D%220%200%20318%20180%22%20preserveAspectRatio%3D%22none%22%3E%3Cdefs%3E%3Cstyle%20type%3D%22text%2Fcss%22%3E%23holder_158bd1d28ef%20text%20%7B%20fill%3Argba(255%2C255%2C255%2C.75)%3Bfont-weight%3Anormal%3Bfont-family%3AHelvetica%2C%20monospace%3Bfont-size%3A16pt%20%7D%20%3C%2Fstyle%3E%3C%2Fdefs%3E%3Cg%20id%3D%22holder_158bd1d28ef%22%3E%3Crect%20width%3D%22318%22%20height%3D%22180%22%20fill%3D%22%23777%22%3E%3C%2Frect%3E%3Cg%3E%3Ctext%20x%3D%22129.359375%22%20y%3D%2297.35%22%3EImage%3C%2Ftext%3E%3C%2Fg%3E%3C%2Fg%3E%3C%2Fsvg%3E"alt="Card image" /> */}
                </LazyLoad>
                <div className="card-body">
                    <b><p className="card-text">{article.description}</p></b>
                </div>
                <ul className="list-group list-group-flush">
                    <li className="list-group-item"><strong>Source : </strong><u>{article.source.name}</u></li>
                </ul>
                <div className="card-body">
                    <a href={article.url} className="card-link" target="_blank" rel="noopener" >Full Article..</a>
                </div>
                <div className="card-footer text-muted">
                </div>
            </div>
        </>
    );
}
export default NewsItem;
