import { useState , useEffect } from 'react';
import Api from './Api';
import Layout from '../components/Layout';

const ApiPages = (props) => {
    let str = ""
    const [pages, setPages] = useState([1, 2, 3, 4, 5]);
    const [page, setPage] = useState(1)
    const [data, setData] = useState(JSON.str)
    useEffect(() => {
        console.log('use')
        return () => {
            var url = 'https://newsapi.org/v2/everything?' +
                'q=tech&' +
                'sortBy=popularity&' +
                'page=' + page + '&' +
                'pageSize=1&' +
                'apiKey=3b214239993247f18926b8fab6ee014f';
            fetch(url).then(response => response.json())
            .then(json => {
                setData(json)
            })
        };
    }, [page])
    const change = () => {
        setPage(pages[0]);
    }
    return (
        <Layout>
            <Api page={page} data={data} />
            <ul className="pagination">
                <li className="page-item disabled">
                    <a className="page-link" href="#">&laquo;</a>
                </li>
                <li className="page-item active">
                    <a className="page-link" onClick={change}>{pages[0]}</a>
                </li>
                <li className="page-item">
                    <a className="page-link" onClick={change}>{pages[1]}</a>
                </li>
                <li className="page-item">
                    <a className="page-link" onClick={change}>{pages[2]}</a>
                </li>
                <li className="page-item">
                    <a className="page-link" onClick={change}>{pages[3]}</a>
                </li>
                <li className="page-item">
                    <a className="page-link" onClick={change}>{pages[4]}</a>
                </li>
                <li className="page-item">
                    <a className="page-link" href="#">&raquo;</a>
                </li>
            </ul>
        </Layout>)
}

export default ApiPages;
