import NewsItem from '../components/NewsItem';

const Api = (props,page) => {
    let arrv = [];
    console.log(props.data)

    /*for(let i of data.articles)
    {
        arrv.push(
         <NewsItem key={i.url} props = {i}/>
        )
    }*/
    return (
        <>
            <h2>NEWS</h2>
            {arrv}
        </>
    )
}
Api.getInititalProps = async function(){
    {
        var page = 0
        var url = 'https://newsapi.org/v2/everything?' +
        'q=tech&' +
        'sortBy=popularity&'+
        'page='+page+'&'+
        'pageSize=1&'+
        'apiKey=3b214239993247f18926b8fab6ee014f';
        const res = await fetch(url);
        const data = await res.json();
        return data
    }
}
export default Api;
