import { useState } from 'react';
import { connect } from 'react-redux'
import { increasePages } from '../components/actions/pagesActions'
import Proptypes from 'prop-types'


const Pagination = (props) => {

    const increaseHandler = () => {
        let arr = []
        for (let i in pages) {
            console.log(arr)
            arr.push(parseInt(pages[i]) + 1)
        }
        console.log(arr)
        setPages(arr)
    }

    const decreaseHandler = () => {
        let arr = []
        for (let i in pages) {
            console.log(arr)
            arr.push(parseInt(pages[i]) - 1)
        }
        console.log(arr)
        setPages(arr)
    }

    const [pages, setPages] = useState([1, 2, 3, 4, 5]);
    return (
        <ul className="pagination">
            <li className={pages[0] === 1 ? "page-item disabled" : "page-item"}>
                <a className="page-link" onClick={decreaseHandler}>-</a>
            </li>
            <li className={pages[0] === page ? "page-item active" : "page-item"}>
                <a className="page-link" onClick={() => props.clickHandler(pages[0])}>{pages[0]}</a>
            </li>
            <li className={pages[1] === page ? "page-item active" : "page-item"}>
                <a className="page-link" onClick={() => props.clickHandler(pages[1])}>{pages[1]}</a>
            </li>
            <li className={pages[2] === page ? "page-item active" : "page-item"}>
                <a className="page-link" onClick={() => props.clickHandler(pages[2])}>{pages[2]}</a>
            </li>
            <li className={pages[3] === page ? "page-item active" : "page-item"}>
                <a className="page-link" onClick={() => props.clickHandler(pages[3])}>{pages[3]}</a>
            </li>
            <li className={pages[4] === page ? "page-item active" : "page-item"}>
                <a className="page-link" onClick={() => props.clickHandler(pages[4])}>{pages[4]}</a>
            </li>
            <li className={pages[4] === 20 ? "page-item disabled" : "page-item"}>
                <a className="page-link" enabled="true" onClick={increaseHandler}>+</a>
            </li>
        </ul>
    )
}

Pagination.propTypes = {
    increasePages: Proptypes.func.isRequired,
    pages: Proptypes.array.isRequired
}

const mapStateToProps = state => ({
    pages: state.pages
})

export default connect(mapStateToProps, { increasePages })(Pagination)
