import NewsItem from '../components/NewsItem';
import React from 'react'
import Fetcher from './Fetcher'

async function Api (props) {
    let arrv = [];
    let data = await Fetcher(props.page)
    console.log(data)
    console.log(props.page) 
    for(let i of data.articles)
    {
        arrv.push(
         <NewsItem key={i.url} props = {i}/>
        )
    }
    return (
        <>
            <h2>NEWS</h2>
            {arrv}
        </>
    )
}
export default Api;
