import fetch from 'isomorphic-unfetch'
import Layout from '../components/Layout';
import NewsItem from '../components/NewsItem';
import ApiPages from '../components/ApiPages';
import React, { useState, useEffect } from 'react';

const Api = () => {
    const [page, setPage] = useState(1);
    let data ;
    useEffect(() => {
        var url = 'https://newsapi.org/v2/everything?' +
            'q=tech&' +
            'sortBy=popularity&' +
            'page=' + '&' +
            'pageSize=1&' +
            'apiKey=3b214239993247f18926b8fab6ee014f';
        const res = fetch(url);
        data = res.json();
    });
    let arrv = [];
    console.log(data)
    /*for (let i of data.articles) {
        arrv.push(
            <NewsItem key={i.url} props={i} />
        )
    }*/
    return (
        <Layout>
            <h2>NEWS</h2>
            {arrv}
            <ApiPages />
        </Layout>
    )
}

Api.getInitialProps = async function () {
    var url = 'https://newsapi.org/v2/everything?' +
        'q=tech&' +
        'sortBy=popularity&' +
        'page=' + '&' +
        'pageSize=1&' +
        'apiKey=3b214239993247f18926b8fab6ee014f';
    const res = await fetch(url);
    const data = await res.json();
    return data
}

export default Api;
