import { useState } from 'react'
import { connect } from 'react-redux'

import Proptypes from 'prop-types'
import { changePage } from "../components/actions/pageAction.js";


const Search = (props) => {

    const handleSubmit = (e) =>{
        e.preventDefault()
        props.changePage(search)
    }
    return (
        <form className="form-inline my-2 my-lg-0" onSubmit = {handleSubmit}>
            <input className="form-control mr-sm-2" type="text" placeholder={props.search}/>
            <button className="btn btn-secondary my-2 my-sm-0" type="submit">Search</button>
        </form>
    )
}

Search.Proptypes = {
    changePage: Proptypes.func.isRequired,
    search: Proptypes.string.isRequired
}

const mapStateToProps = state => ({
    search: state.page.search
})

export default connect(mapStateToProps, { changePage })(Search)
