export default class Api extends Component {

    constructor(props) {
        super(props)

        this.state = {
            arrv:[]
        }
    }


    componentWillMount() {
        let data = Fetcher(this.props.page);
        data.then(function (a) {
            console.log(this.props.page)
            console.log(a)
            for (let i of a.articles) {
                this.setState({
                    arrv: this.state.arrv.push(
                        <NewsItem key={i.url} props={i} />
                    )
                })

            }
        })
    }
    render() {
        return (
            <>
                <h2>NEWS</h2>
                {arrv}
            </>
        )
    }
}
