import { useState, useEffect } from 'react';
import Api from '../components/Api';
import Layout from '../components/Layout';
import fetch from 'isomorphic-unfetch'
import React, { Component } from 'react'

export default class ApiPages extends Component {

    constructor(props) {
        super(props)
            this.data
        this.state = {
            page: 1,
            data: [],
            pages: [1, 2, 3, 4, 5, 6]
        }
    }

    clickHandler(p) {
        this.setState({
            page: p,
        })
    }

    componentWillMount() {
        var url = 'https://newsapi.org/v2/everything?' +
            'q=tech&' +
            'sortBy=popularity&' +
            'page=1&' +
            'pageSize=1&' +
            'apiKey=3b214239993247f18926b8fab6ee014f';
        fetch(url).then(response => response.json())
            .then(json => {
                this.data=json
                //console.log(this.data)
            })
    }

    componentWillUpdate() {

        var url = 'https://newsapi.org/v2/everything?' +
            'q=tech&' +
            'sortBy=popularity&' +
            'page=' + this.state.page + '&' +
            'pageSize=1&' +
            'apiKey=3b214239993247f18926b8fab6ee014f';
            fetch(url).then(response => response.json())
            .then(json => {
                this.data=json
                //console.log(this.data)
            })
    }

    render() {
        return (
            <Layout>
                <Api page={this.state.page} data={this.data} />
                <ul className="pagination">
                    <li className="page-item disabled">
                        <a className="page-link" href="#">&laquo;</a>
                    </li>
                    <li className="page-item active">
                        <a className="page-link" onClick={() => this.clickHandler(this.state.pages[0])}>{this.state.pages[0]}</a>
                    </li>
                    <li className="page-item">
                        <a className="page-link" onClick={() => this.clickHandler(this.state.pages[1])}>{this.state.pages[1]}</a>
                    </li>
                    <li className="page-item">
                        <a className="page-link" onClick={() => this.clickHandler(this.state.pages[2])}>{this.state.pages[2]}</a>
                    </li>
                    <li className="page-item">
                        <a className="page-link" onClick={() => this.clickHandler(this.state.pages[3])}>{this.state.pages[3]}</a>
                    </li>
                    <li className="page-item">
                        <a className="page-link" onClick={() => this.clickHandler(this.state.pages[4])}>{this.state.pages[4]}</a>
                    </li>
                    <li className="page-item">
                        <a className="page-link" href="#">&raquo;</a>
                    </li>
                </ul>
            </Layout >
        )
    }
}



