import { useState, useEffect } from 'react';
import Api from '../components/Api';
import Layout from '../components/Layout';
import React, { Component } from 'react'

const ApiPages = () => {
    const [pages, setPages] = useState([1, 2, 3, 4, 5]);
    const [page, setPage] = useState(1)
    const clickHandler = (p) => {
        setPage(p);
    }
    return (
        <Layout>
            <Api page={page}/>
            <ul className="pagination">
                <li className="page-item disabled">
                    <a className="page-link" href="#">&laquo;</a>
                </li>
                <li className="page-item active">
                    <a className="page-link" onClick={() => clickHandler(pages[0])}>{pages[0]}</a>
                </li>
                <li className="page-item">
                    <a className="page-link" onClick={() => clickHandler(pages[1])}>{pages[0]}</a>
                </li>
                <li className="page-item">
                    <a className="page-link" onClick={() => clickHandler(pages[2])}>{pages[0]}</a>
                </li>
                <li className="page-item">
                    <a className="page-link" onClick={() => clickHandler(pages[3])}>{pages[0]}</a>
                </li>
                <li className="page-item">
                    <a className="page-link" onClick={() => clickHandler(pages[4])}>{pages[0]}</a>
                </li>
                <li className="page-item">
                    <a className="page-link" href="#">&raquo;</a>
                </li>
            </ul>
        </Layout>)
}

export default ApiPages;
