import Layout from '../components/Layout';
import NewsItem from '../components/NewsItem';
import ApiPages from '../components/ApiPages';
import React,{ useState , useEffect } from 'react';
import getData from '../components/GetData';

const Api = (props) => {
    const [page, setPage] = useState(1)
    var data
    useEffect(() => {
        return () => {
            console.log('called')
            console.log(getData(page))
            data = getData(page)
        };
    })
    let arrv = [];
    console.log(data)
    const currPage = (p) =>
    {
        console.log('called1')
        setPage(p)
        data = getData(page)
    }

    /*for(let i of data.articles)
    {
        arrv.push(
         <NewsItem key={i.url} props = {i}/>
        )
    }*/
    
    

    return (
        <Layout>
            <h2>NEWS</h2>
            {arrv}
            <ApiPages callbackfn = {currPage}/>
            </Layout>
    )
}
Api.getInititalProps = async function(){
    {
        var url = 'https://newsapi.org/v2/everything?' +
        'q=tech&' +
        'sortBy=popularity&'+
        'page='+page+'&'+
        'pageSize=1&'+
        'apiKey=3b214239993247f18926b8fab6ee014f';
        const res = await fetch(url);
        const data = await res.json();
        return data
    }
}
export default Api;
