import NewsItem from '../components/NewsItem';
import ApiPages from '../components/ApiPages';
import { useState } from 'react';
import getData from '../components/GetData';

const Api = ({data}) => {
    const [page, setPage] = useState(0)
    let arrv = [];
    console.log(data)
    const currPage = (p) =>
    {
        setPage(p)
        data = getData(page)
    }
    /*for(let i of data.articles)
    {
        arrv.push(
         <NewsItem key={i.url} props = {i}/>
        )
    }*/

    

    return (
        <Layout>
            <h2>NEWS</h2>
            {arrv}
            <ApiPages callbackfn = {currPage}/>
            </Layout>
    )
}

Api.getInitialProps = async function () {
    const data = await getData(1)
    return data
}

export default Api;
