import Layout from '../components/Layout';
import NewsItem from '../components/NewsItem';
import ApiPages from '../components/ApiPages';
import { useState , useEffect } from 'react';
import getData from '../components/GetData';

const Api = () => {
    const [page, setPage] = useState(0)
    var data = JSON
    useEffect(() => {
        return () => {
            data = getData(page)
        };
    })
    let arrv = [];
    console.log(data)
    const currPage = (p) =>
    {
        setPage(p)
        data = getData(page)
    }

    for(let i of data.articles)
    {
        arrv.push(
         <NewsItem key={i.url} props = {i}/>
        )
    }
    
    

    return (
        <Layout>
            <h2>NEWS</h2>
            {arrv}
            <ApiPages callbackfn = {currPage}/>
            </Layout>
    )
}

export default Api;
