import Head from 'next/head'
import fetch from 'isomorphic-unfetch'
import { useEffect, useState, useRef } from 'react'
import { connect } from 'react-redux'
import Api from '../components/Api';
import Layout from '../components/Layout';
import Search from '../components/Search';
import Pagination from '../components/Pagination'

const ApiPages = (props) => {
    console.log('hmm')
    const [data, setData] = useState(props.data)
    const mounted = useRef();
    useEffect(() => {
        if (!props.isFetched || mounted.current)
            ApiPages.getInitialProps({ page: props.page, search: props.search, isUser: 1 })
                .then(json => setData(json.data))
        else
            mounted.current = true;
    }, [props.page, props.search])
    console.log('okay')
    return (
        <>
            <Head>
                <meta keywords="API" />
            </Head>
            <Layout>
                <h2>NEWS</h2>
                <br />
                <Search />
                <br />
                <Pagination />
                <Api data={data} />
                <Pagination />
            </Layout>
        </>)
}

ApiPages.getInitialProps = async function ({ search = 'tech', page = 1, isUser = 0 }) {

    if (isUser == 0 && !(typeof window === 'undefined'))
        return { data: {}, isFetched: false }

    var url = 'https://newsapi.org/v2/everything?' +
        'q=' + search + '&' +
        'sortBy=publishedAt&' +
        'page=' + page + '&' +
        'pageSize=10&language=en&' +
        'apiKey=3b214239993247f18926b8fab6ee014f';
    const res = await fetch(url);
    const json = await res.json()
    return { data: json, isFetched: true }

}
const mapStateToProps = state => ({
    page: state.page.page,
    search: state.page.search
})

export default connect(mapStateToProps, null)(ApiPages)
