import { useState, useEffect } from 'react';
import Api from '../components/Api';
import Layout from '../components/Layout';
import Search from '../components/Search';
import Pagination from '../components/Pagination'

const ApiPages = () => {
    const [pages, setPages] = useState([1, 2, 3, 4, 5]);
    const [page, setPage] = useState(1)
    const [search, setSearch] = useState('tech')

    useEffect(() => {
        window.onbeforeunload = (event) => {
            console.log('triggered')
            //event.preventDefault()
            return '';
        };
    }, [])

    const clickHandler = (p) => {
        window.scrollTo(0, 0)
        setPage(p);
    }

    const increaseHandler = () => {
        let arr = []
        for (let i in pages) {
            console.log(arr)
            arr.push(parseInt(pages[i]) + 1)
        }
        console.log(arr)
        setPages(arr)
    }

    const decreaseHandler = () => {
        let arr = []
        for (let i in pages) {
            console.log(arr)
            arr.push(parseInt(pages[i]) - 1)
        }
        console.log(arr)
        setPages(arr)
    }

    const submitInput = (s) => {
        console.log('okay')
        setSearch(s)

    }
    return (
        <Layout>
            <h2>NEWS</h2>
            <br />
            <Search submitInput={submitInput} />
            <br />
            <Pagination increaseHandler={increaseHandler} decreaseHandler={decreaseHandler} clickHandler={clickHandler} pages={pages} page={page} />
            <Api page={page} search={search} />
            <Pagination increaseHandler={increaseHandler} decreaseHandler={decreaseHandler} clickHandler={clickHandler} pages={pages} page={page} />
        </Layout>)
}

export default ApiPages;
